mapper.select:
select  oa_jwb_grade.name as banji,  from  oa_jzb_student 	
INNER JOIN oa_jwb_grade ON oa_jwb_grade.id= oa_jzb_student.grade_id 
where oa_jwb_grade.struts =3 and oa_jwb_grade.id=#{id}

SELECT NAME FROM oa_jwb_grade INNER JOIN oa_rlzyb_user ON oa_rlzyb_user.emp_id = oa_jwb_grade.obtainID
AND oa_rlzyb_user.emp_id=#{empTecid}
and oa_jwb_grade.struts > 2

SELECT * from ( SELECT * FROM ( select oa_rlzyb_user.emp_id, oa_jwb_grade.struts, oa_jwb_grade.name as className,oa_scb_education.education as record, oa_jzb_student.stu_Id, oa_jzb_student.stu_number, oa_jzb_student.stu_name,oa_jzb_student.spellName, oa_jzb_student.contact_way, oa_jzb_student.education_id,oa_jzb_student.grade_id,oa_jzb_student.stu_sex, oa_jzb_student.intentional_City,oa_jzb_student.address,oa_jzb_student.ID_number,oa_jzb_student.stu_age,
from oa_jyb_employment INNER JOIN oa_jzb_student ON oa_jzb_student.stu_Id=oa_jyb_employment.employment_studentId INNER JOIN oa_jwb_grade ON oa_jwb_grade.id= oa_jzb_student.grade_id LEFT JOIN oa_scb_education ON oa_scb_education.id = oa_jzb_student.education_id INNER JOIN oa_rlzyb_user ON oa_rlzyb_user.emp_id = oa_jwb_grade.obtainID ORDER BY oa_jyb_employment.employment_time DESC ) emp1 GROUP BY className, stu_number) emp2 where struts > 2 and employment_status ='待业'
and className = #{employments.className}
and stu_age < 18
and stu_age >= 18 and stu_age <= 20
and stu_age > 20 and stu_age <= 24
and stu_age > 24
and spellName like concat('%',#{sname1},'%')
AND emp_id=#{empTecid}
limit #{nowPage},#{pageSize}

SELECT * from ( SELECT * FROM ( select oa_rlzyb_user.emp_id, oa_jwb_grade.struts, oa_jwb_grade.name as className,oa_scb_education.education as record, oa_jzb_student.stu_Id, oa_jzb_student.stu_number, oa_jzb_student.stu_name,oa_jzb_student.spellName, oa_jzb_student.contact_way , oa_jzb_student.education_id,oa_jzb_student.grade_id,oa_jzb_student.stu_sex, oa_jzb_student.intentional_City,oa_jzb_student.address,oa_jzb_student.ID_number,oa_jzb_student.stu_age,
from oa_jyb_employment INNER JOIN oa_jzb_student ON oa_jzb_student.stu_Id=oa_jyb_employment.employment_studentId INNER JOIN oa_jwb_grade ON oa_jwb_grade.id= oa_jzb_student.grade_id left JOIN oa_scb_education ON oa_scb_education.id = oa_jzb_student.education_id INNER JOIN oa_rlzyb_user ON oa_rlzyb_user.emp_id = oa_jwb_grade.obtainID ORDER BY oa_jyb_employment.employment_time DESC ) emp1 GROUP BY className, stu_number) emp2
and employment_city = #{employments.employment_city}
and intentional_City = #{intentional_City}
and record = #{employments.record}
and className = #{employments.className}
and employment_engaged_type = #{employments.employment_engaged_type}
and stu_age < 18
and stu_age >= 18 and stu_age <= 20
and stu_age > 20 and stu_age <= 24
and stu_age > 24
and employment_Salary_worker >= 2000 and employment_Salary_worker <= 4000
and employment_Salary_worker > 4000 and employment_Salary_worker <= 6000
and employment_Salary_worker > 6000 and employment_Salary_worker <= 8000
and employment_Salary_worker > 8000
and spellName like concat('%',#{sname1},'%')
and spellName like concat('%',#{sname2},'%')
and employment_status =#{employments.employment_status}
AND emp_id =#{empTecid}
and struts > 2
limit #{nowPage},#{pageSize}

 SELECT COUNT(counts) FROM
 (SELECT oa_jzb_student.stu_Id ,COUNT(*) counts FROM oa_jyb_employment
 INNER JOIN oa_jzb_student ON oa_jzb_student.stu_Id=oa_jyb_employment.employment_studentId 
 INNER JOIN oa_jwb_grade ON oa_jwb_grade.id= oa_jzb_student.grade_id 
 left JOIN oa_scb_education ON oa_scb_education.id = oa_jzb_student.education_id	
   INNER JOIN oa_rlzyb_user ON oa_rlzyb_user.emp_id = oa_jwb_grade.obtainID	    
 
	    
         and employment_city = #{employments.employment_city}
          
             
        
         and oa_jzb_student.intentional_City = #{intentional_City}
        
                
        
         and oa_scb_education.education = #{employments.record}
          
        
        
         and oa_jwb_grade.name = #{employments.className}
          
        
        
         and employment_engaged_type = #{employments.employment_engaged_type}
         
        
        
         and oa_jzb_student.stu_age < 18
          
        
         and oa_jzb_student.stu_age >= 18 and oa_jzb_student.stu_age <= 20
          
        
         and oa_jzb_student.stu_age > 20 and oa_jzb_student.stu_age <= 24
          
        
         and oa_jzb_student.stu_age > 24
          
        
        
          and employment_Salary_worker >= 2000 and employment_Salary_worker <= 4000
          
        
         and employment_Salary_worker > 4000 and employment_Salary_worker <= 6000
          
        
         and employment_Salary_worker > 6000 and employment_Salary_worker <= 8000
          
        
         and employment_Salary_worker > 8000
        
        
        
         and oa_jzb_student.spellName like concat('%',#{sname2},'%')
        
        
        
		and employment_status =#{employments.employment_status}		
		
	    
         AND oa_rlzyb_user.emp_id =#{empTecid} 
        
         and oa_jwb_grade.struts > 2 

	 
   
 GROUP BY oa_jzb_student.stu_number 
  ) t

SELECT COUNT(1) FROM ( SELECT * FROM ( select oa_jwb_grade.name as className , oa_jzb_student.stu_Id, oa_jzb_student.stu_number, oa_jzb_student.stu_name,oa_jzb_student.spellName, oa_jzb_student.stu_age,oa_jzb_student.contact_way , oa_scb_education.education as record , employment_id, employment_studentId, employment_time, employment_city, employment_unit, employment_Salary_probation, employment_Salary_worker, employment_engaged_type, employment_welfare, employment_remarks,employment_engaged ,employment_status from oa_jyb_employment INNER JOIN oa_jzb_student ON oa_jzb_student.stu_Id=oa_jyb_employment.employment_studentId INNER JOIN oa_jwb_grade ON oa_jwb_grade.id= oa_jzb_student.grade_id INNER JOIN oa_scb_education ON oa_scb_education.id = oa_jzb_student.education_id where employment_status= '已就业' OR employment_status='自主' ORDER BY oa_jyb_employment.employment_time DESC ) emp GROUP BY stu_number ) empstat
and className = #{className}
and employment_status= #{employment_status}
and employment_city = #{employment_city}
and record=#{record}
and employment_Salary_worker >= 2000 and employment_Salary_worker <= 4000
and employment_Salary_worker > 4000 and employment_Salary_worker <= 6000
and employment_Salary_worker > 6000 and employment_Salary_worker <= 8000
and employment_Salary_worker > 8000
and stu_age < 18
and stu_age >= 18 and stu_age <= 20
and stu_age > 20 and stu_age <= 24
and stu_age > 24

